function setBodyLoaded(){document.getElementsByTagName("body")[0].classList.add("loaded")}function setAnimatedBlockLoaded(){const e=document.getElementById("animatedBlockAdventages"),d=document.getElementById("animatedBlock");if(!d)return;let t=!1,n=!1;window.addEventListener("scroll",()=>{window.pageYOffset+window.innerHeight>d.offsetTop+300&&(n||(d.classList.add("loaded"),n=!0)),window.pageYOffset+window.innerHeight>e.offsetTop+150&&(t||(e.classList.add("loaded"),t=!0))})}window.addEventListener("load",(function(){setBodyLoaded(),setAnimatedBlockLoaded()}));